mapper.select:
select

from pmt_payment_order
where id = #{id,jdbcType=BIGINT}

select

from pmt_payment_order
where message_id = #{messageId,jdbcType=VARCHAR}

select

from
pmt_payment_order
left join
pmt_payment_channel on pmt_payment_order.channel_code = pmt_payment_channel.channel_code
WHERE 1 = 1

	and pmt_payment_order.order_number = #{orderNumber,jdbcType=VARCHAR}


	and pmt_payment_order.status in
	
		#{status.value}
	


	and pmt_payment_channel.channel_code in
	
		#{channelCode,jdbcType=VARCHAR}
	


	and pmt_payment_order.create_time > #{startDate,jdbcType=VARCHAR}


	and pmt_payment_order.create_time < #{endDate,jdbcType=VARCHAR}

order by
pmt_payment_order.create_time desc

select
from pmt_payment_order left join pmt_payment_order_item on pmt_payment_order_item.payment_order_id = pmt_payment_order.id left join pmt_payment_transaction on pmt_payment_transaction.payment_order_id = pmt_payment_order.id left join pmt_payment_channel on pmt_payment_transaction.payment_channel_id = pmt_payment_channel.id LEFT JOIN pmt_payment_way on pmt_payment_transaction.payment_way_id = pmt_payment_way.id where 1=1
and pmt_payment_order.order_number = #{orderNumber,jdbcType=VARCHAR}
and pmt_payment_order_item.application_number = #{applicationNumber,jdbcType=VARCHAR}
and pmt_payment_transaction.payment_status = #{status.value}
order by pmt_payment_order.create_time desc

SELECT paymentOrder.status,count(*) from pty_payment_order paymentOrder LEFT JOIN pty_payment_order_item paymentItem on paymentOrder.id=paymentItem.payment_order_id LEFT JOIN pty_payment_transaction t on t.payment_order_id = paymentOrder.id where 1=1
paymentOrder.order_number =#{orderNumber}
GROUP BY paymentOrder.status

select

from
pmt_payment_order
left join
pmt_payment_channel on pmt_payment_order.channel_code = pmt_payment_channel.channel_code
WHERE 1 = 1

	and pmt_payment_order.order_number = #{orderNumber,jdbcType=VARCHAR}


	and pmt_payment_channel.channel_code in
	
		#{channelCode,jdbcType=VARCHAR}
	


	and pmt_payment_order.status in
	
		#{payStatus.value}
	


	and pmt_payment_order.check_status in
	
		#{checkStatus.value}
	


	and pmt_payment_order.check_time > #{startDate,jdbcType=VARCHAR}


	and pmt_payment_order.check_time < #{endDate,jdbcType=VARCHAR}

order by
pmt_payment_order.check_status desc , pmt_payment_order.pay_time desc
