mapper.select:
select
from pmt_refund_application left JOIN pmt_payment_order on pmt_refund_application.order_number = pmt_payment_order.order_number left JOIN pmt_payment_channel on pmt_payment_order.channel_code = pmt_payment_channel.channel_code left JOIN pmt_payment_transaction on pmt_refund_application.refund_application_number = pmt_payment_transaction.internal_transaction_number where 1=1
and pmt_refund_application.order_number =#{orderNumber}
and pmt_refund_application.refund_application_number = #{refundNumber}
and pmt_payment_channel.channel_code in
#{channelCode}
and pmt_refund_application.create_date > #{startDate,jdbcType=VARCHAR}
and pmt_refund_application.create_date < #{endDate,jdbcType=VARCHAR}
order by pmt_refund_application.create_date desc

select

from pmt_refund_application
where 1=1

	and pmt_refund_application.order_number =#{orderNumber}


	and pmt_refund_application.refund_application_number = #{refundApplicationNumber}

select

from pmt_refund_application
where 1=1

	and pmt_refund_application.order_number =#{orderNumber}


	and pmt_refund_application.status = #{status.value}
